import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import { v4 as uuidv4 } from 'uuid';
import { useHttp } from '../hooks/useHttp';

const DataForm = ({ createTodo }) => {
  const { sendRequest } = useHttp();
  const [data, setData] = useState({
    Description: '',
    Module: '',
    Priority: '',
    RequestedPerson: '',
  });

  const [errorMsg, setErrorMsg] = useState('');
  const { Description, Module, Priority, RequestedPerson } = data;

  const handleOnSubmit = async (event) => {
    event.preventDefault();
    const values = [Description, Module, Priority, RequestedPerson];
    let errorMsg = '';

    const allFieldsFilled = values.every((field) => {
      const value = `${field}`.trim();
      return value !== '' && value !== '0';
    });

    if (allFieldsFilled) {
      const todoData = {
        id: uuidv4(),
        Description,
        Priority,
        Module,
        date: new Date(),
      };

      createTodo(todoData); // Call the createTodo function from TodoApp

      // Reset the form data
      setData({
        Description: '',
        Module: '',
        Priority: '',
        RequestedPerson: '',
      });
    } else {
      errorMsg = 'Please fill out all the fields.';
    }
    setErrorMsg(errorMsg);
  };
  
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    switch (name) {
      case 'quantity':
        if (value === '' || parseInt(value) === +value) {
          setData((prevState) => ({
            ...prevState,
            [name]: value
          }));
        }
        break;
      case 'price':
        if (value === '' || value.match(/^\d{1,}(\.\d{0,2})?$/)) {
          setData((prevState) => ({
            ...prevState,
            [name]: value
          }));
        }
        break;
      case 'Description':
      case 'Module':
      case 'Priority':
      case 'RequestedPerson':
        setData((prevState) => ({
          ...prevState,
          [name]: value
        }));
        break;
      default:
        break;
    }
  };
  return (
    <div className="main-form">
      {errorMsg && <p className="errorMsg">{errorMsg}</p>}
      <Form onSubmit={handleOnSubmit}>
        <Form.Group controlId="RequestedPerson">
          <Form.Label>Проблема</Form.Label>
          <Form.Control
            className="input-control"
            type="text"
            name="RequestedPerson"
            value={RequestedPerson}
            placeholder="Имя"
            onChange={handleInputChange}
          />
        </Form.Group> 
        <Form.Group controlId="Module">
          <Form.Label>Модуль</Form.Label>
          <Form.Control
            className="input-control"
            type="string"
            name="Module"
            value={Module}
            placeholder="Модуль | ICC | Панель"
            onChange={handleInputChange}
            />
        </Form.Group>
     
        <Form.Group controlId="Description">
          <Form.Label>Описание</Form.Label>
          <Form.Control
            className="input-control"
            type="string"
            name="Description"
            value={Description}
            placeholder="Описание"
            onChange={handleInputChange}
          />
        </Form.Group>
   
        <Form.Group controlId="Priority">
          <Form.Label>Приоритет</Form.Label>
          <Form.Control
            className="input-control"
            type="string"
            name="Priority"
            value={Priority}
            placeholder="Приоритет"
            onChange={handleInputChange}
          />
        </Form.Group>
       
        <Button variant="primary" type="submit" className="submit-btn">
          Отправить
        </Button>
      </Form>
    </div>
  );
};

export default DataForm;
