  };

  return (
    <React.Fragment>
      <div className="book-list">
        {!_.isEmpty(datas) ? (
          books.map((data) => (
            <Book key={data.id} {...data} handleRemoveBook={handleRemoveBook} />
          ))
        ) : (
          <p className="message">No books available. Please add some books.</p>
        )}
      </div>
    </React.Fragment>
  );
};

export default BooksList;
